import{_ as e,c as t,o as a,a4 as o}from"./chunks/framework.uQk9_EO2.js";const g=JSON.parse('{"title":"Examples and Tutorials","description":"","frontmatter":{},"headers":[],"relativePath":"en/cases-and-tutorials/index.md","filePath":"en/cases-and-tutorials/index.md","lastUpdated":1712305443000}'),r={name:"en/cases-and-tutorials/index.md"},s=o('<h1 id="examples-and-tutorials" tabindex="-1">Examples and Tutorials <a class="header-anchor" href="#examples-and-tutorials" aria-label="Permalink to &quot;Examples and Tutorials&quot;">​</a></h1><h2 id="examples" tabindex="-1">Examples <a class="header-anchor" href="#examples" aria-label="Permalink to &quot;Examples&quot;">​</a></h2><ul><li><strong>One Step, Two Steps</strong>(<a href="https://github.com/cocos/tutorial-mind-your-step-3d" target="_blank" rel="noreferrer">GitHub</a>): A game with step-by-step tutorial in the <a href="./../getting-started/first-game/">Quick Start</a>.</li><li><strong>Material Examples</strong>(<a href="https://github.com/cocos/cocos-example-materials" target="_blank" rel="noreferrer">GitHub</a>): Shows a variety of engine-built and customized material effects, including standard PBR, cartoon rendering, and also includes advanced materials such as skin, hair, eyeballs, leaves, and more.</li><li><strong>Render Pipeline Usage Demo</strong>(<a href="https://github.com/cocos/cocos-example-render-pipeline" target="_blank" rel="noreferrer">GitHub</a>): Demonstrates in Sponza how to use builtin post process render pipeline with all post-process features enabled.</li><li><strong>Examples of Physics</strong>(<a href="https://github.com/cocos/cocos-example-physics" target="_blank" rel="noreferrer">GitHub</a>): Includes 2D and 3D physics test cases and examples, such as <strong>Engulfing Black Hole</strong>, <strong>Simple Car</strong>, <strong>Falling ball</strong>, etc. The test cases introduce some basic functions and usage methods. It helps you understand the physical functions with documentation easily.</li><li><strong>Marionette Animation Examples</strong>(<a href="https://github.com/cocos/cocos-example-marionette" target="_blank" rel="noreferrer">GitHub</a>): Demonstrates how to use <a href="./../animation/marionette/">Marionette Aniamtion System</a> in details, including state machine, pose graph, events, IK, etc.</li><li><strong>UI Example</strong>（<a href="https://github.com/cocos/cocos-example-ui/" target="_blank" rel="noreferrer">GitHub</a> | <a href="https://store.cocos.com/app/detail/2799" target="_blank" rel="noreferrer">Cocos Store</a>）： A complex UI example, including the home page, store, backpack, and other common game UI interface.</li><li><strong>Example Cases</strong>(<a href="https://github.com/cocos/cocos-example-projects" target="_blank" rel="noreferrer">GitHub</a>): Includes examples of networking, npm modules, and other features.</li><li><strong>Modules Test Cases</strong>(<a href="https://github.com/cocos/cocos-test-projects" target="_blank" rel="noreferrer">GitHub</a>): The example project of each function of the engine, which basically covers most of the function modules of the engine. Users can refer to the usage in this project when using the functions for development.</li><li><strong>Practical Solutions</strong>(<a href="https://github.com/cocos/cocos-awesome-tech-solutions" target="_blank" rel="noreferrer">GitHub</a>): A collection of practical solutions provided by technical support engineers.</li><li><strong>Jump Ball 3D</strong>(<a href="https://github.com/cocos/cocos-example-ball" target="_blank" rel="noreferrer">GitHub</a> | <a href="https://store.cocos.com/app/detail/2802" target="_blank" rel="noreferrer">Cocos Store</a>): Users can make jump ball games through this project.</li><li><strong>Taxi Game 3D</strong>(<a href="https://github.com/cocos/cocos-tutorial-taxi-game" target="_blank" rel="noreferrer">GitHub</a> | <a href="https://store.cocos.com/app/en/detail/2796" target="_blank" rel="noreferrer">Cocos Store</a>): A physics-based 3d game, users can make taxi games through this project.</li></ul><blockquote><p>All cases and examples on GitHub are updated with the version, and the main branch corresponds to the latest <strong>Cocos Creator</strong> version, so please pay attention when downloading.</p></blockquote><h2 id="tutorials" tabindex="-1">Tutorials <a class="header-anchor" href="#tutorials" aria-label="Permalink to &quot;Tutorials&quot;">​</a></h2><h3 id="videos" tabindex="-1">Videos <a class="header-anchor" href="#videos" aria-label="Permalink to &quot;Videos&quot;">​</a></h3><ul><li><a href="https://www.youtube.com/watch?v=JSOXYPqZ1-8&amp;list=PLbvpmJKjO3NA4dlW43GzhJUMaXyIp3xpc" target="_blank" rel="noreferrer">Cocos Creator 3.x for Beginners</a></li><li><a href="https://www.youtube.com/watch?v=R4Mxm55x56Q&amp;list=PLbvpmJKjO3NCgicfQ_N32Oo62LgPVs4kf" target="_blank" rel="noreferrer">Cocos Quick Look</a></li><li><a href="https://www.youtube.com/watch?v=tUQHK42UcHc&amp;list=PLbvpmJKjO3ND91HTUSKWNzMboob-7J_wq" target="_blank" rel="noreferrer">Introduction to Shaders with Cocos Creator</a></li><li><a href="https://www.youtube.com/watch?v=3v4pq0tDo5g&amp;list=PLbvpmJKjO3NDv_eb6vlN70Zo-pdAv8MNW" target="_blank" rel="noreferrer">Introduction to Cocos Creator 3.0</a></li></ul><h3 id="articles" tabindex="-1">Articles <a class="header-anchor" href="#articles" aria-label="Permalink to &quot;Articles&quot;">​</a></h3><ul><li><a href="https://www.cocos.com/en/post/33e9da7fc3825a8aeb66ff6fbb7f5dd4" target="_blank" rel="noreferrer">Best Tutorials - Part One</a></li><li><a href="https://www.cocos.com/en/post/52fnhtaqlEYYTafjBYjMMYWKyA6d3qIZ" target="_blank" rel="noreferrer">Best Tutorials - Part Two</a></li></ul><h2 id="more" tabindex="-1">More <a class="header-anchor" href="#more" aria-label="Permalink to &quot;More&quot;">​</a></h2><ul><li><a href="https://discuss.cocos2d-x.org/c/creator/33" target="_blank" rel="noreferrer">Cocos Forum</a>： ask questions, find answers, communicate with other developers.</li><li><a href="http://store.cocos.com/" target="_blank" rel="noreferrer">Cocos Store</a>： various art assets, extensions, source codes and learning demos.</li><li><a href="https://www.youtube.com/@CocosEngine" target="_blank" rel="noreferrer">Youtube channel — Cocos</a>：news and tutorials.</li></ul>',11),n=[s];function l(i,c,h,u,p,m){return a(),t("div",null,n)}const f=e(r,[["render",l]]);export{g as __pageData,f as default};
