import{_ as e,c as t,o as n,a4 as r}from"./chunks/framework.uQk9_EO2.js";const s="/docs/cocos/assets/batched-skinning-model-component.Bqu55T_n.png",y=JSON.parse('{"title":"SkinnedMeshBatchRenderer","description":"","frontmatter":{},"headers":[],"relativePath":"en/module-map/mesh/skinnedMeshBatchRenderer.md","filePath":"en/module-map/mesh/skinnedMeshBatchRenderer.md","lastUpdated":1712305443000}'),a={name:"en/module-map/mesh/skinnedMeshBatchRenderer.md"},o=r('<h1 id="skinnedmeshbatchrenderer" tabindex="-1">SkinnedMeshBatchRenderer <a class="header-anchor" href="#skinnedmeshbatchrenderer" aria-label="Permalink to &quot;SkinnedMeshBatchRenderer&quot;">​</a></h1><p>The SkinnedMeshBatchRenderer component is used to combine the rendering of all meshes controlled by the same skeletal animation component.</p><p><img src="'+s+'" alt="mesh batch"></p><table><thead><tr><th style="text-align:left;">Properties</th><th style="text-align:left;">Description</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>Operation</strong></td><td style="text-align:left;">Any changes to the property will not take effect immediately and will need to be recalculated by tapping the <strong>Cook</strong> button before they can be applied.</td></tr><tr><td style="text-align:left;"><strong>Materials</strong></td><td style="text-align:left;">The &quot;master material&quot; used after the merge requires the use of your own customized merge effect.</td></tr><tr><td style="text-align:left;"><strong>LightmapSettings</strong></td><td style="text-align:left;">For baking Lightmap, please refer to <a href="./../../concepts/scene/light/lightmap">Lightmapping</a> for details.</td></tr><tr><td style="text-align:left;"><strong>ShadowCastingMode</strong></td><td style="text-align:left;">Specify whether the current model will cast shadows, you need to <a href="./../../concepts/scene/light/shadow#Enable Shadow Effect">Enable Shadow Effect</a> in the scene first。</td></tr><tr><td style="text-align:left;"><strong>ReceiveShadow</strong></td><td style="text-align:left;">Specify whether the current model will receive and display shadow effects generated by other objects, you need to <a href="./../../concepts/scene/light/shadow#Enable Shadow Effect">Enable Shadow Effect</a> in the scene first. This property only takes effect when the shadow type is <strong>ShadowMap</strong>.</td></tr><tr><td style="text-align:left;"><strong>SkinningRoot</strong></td><td style="text-align:left;">The root node of the skeletal skin, usually the node where the SkeletalAnimation component is located.</td></tr><tr><td style="text-align:left;"><strong>AtlasSize</strong></td><td style="text-align:left;">The edge length of the final set generated by the combined atlas.</td></tr><tr><td style="text-align:left;"><strong>BatchableTextureNames</strong></td><td style="text-align:left;">The texture properties of the material that are actually involved in the merge, and the properties that are not involved in the merge, use the first unit&#39;s texture uniformly.</td></tr><tr><td style="text-align:left;"><strong>Units</strong></td><td style="text-align:left;">The sub-model data before the combined batch, is the main data source.</td></tr><tr><td style="text-align:left;"><strong>Mesh</strong></td><td style="text-align:left;">The model data of current sub-mesh, usually directly from glTF or FBX model file.</td></tr><tr><td style="text-align:left;"><strong>Skeleton</strong></td><td style="text-align:left;">The skeleton data of the current model, usually directly from the glTF or FBX model file.</td></tr><tr><td style="text-align:left;"><strong>Materials</strong></td><td style="text-align:left;">The &quot;sub-materials&quot; used by the current sub-model, which is a non-batching effect, the effect used by different sub-meshes should be consistent.</td></tr><tr><td style="text-align:left;"><strong>Offset</strong></td><td style="text-align:left;">The offset of the current sub-model when it is merged, with the top-left corner of the atlas as the origin, in the range <code>[0, 1]</code>. For example, the data in the figure represents that the sub-map is overlapped with the top-left corner of the atlas.</td></tr><tr><td style="text-align:left;"><strong>Size</strong></td><td style="text-align:left;">The size of the current sub-model, in the range <code>[0, 1]</code>. For example, the data in the figure represents 1/2 of the entire set.</td></tr><tr><td style="text-align:left;"><strong>CopyFrom</strong></td><td style="text-align:left;">The target properties (except <code>offset</code> and <code>size</code>) can be copied automatically by dragging in <strong>SkinningModelComponent</strong> for easy operation.</td></tr></tbody></table><p>Please refer to <a href="./../../animation/skeletal-animation">BatchedSkinnedMeshRenderer</a> for more details.</p><p>For API reference, please refer to <a href="./__APIDOC__/en/class/SkinnedMeshBatchRenderer">SkinnedMeshBatchRenderer API</a>.</p>',6),l=[o];function d(i,h,c,f,g,m){return n(),t("div",null,l)}const u=e(a,[["render",d]]);export{y as __pageData,u as default};
