import{_ as t,c as e,o as l,a4 as r}from"./chunks/framework.uQk9_EO2.js";const a="/docs/cocos/assets/2d-particle.D0VDOCMY.png",m=JSON.parse('{"title":"ParticleSystem2D Component Reference","description":"","frontmatter":{},"headers":[],"relativePath":"en/particle-system/2d-particle/2d-particle.md","filePath":"en/particle-system/2d-particle/2d-particle.md","lastUpdated":1712305443000}'),i={name:"en/particle-system/2d-particle/2d-particle.md"},s=r('<h1 id="particlesystem2d-component-reference" tabindex="-1">ParticleSystem2D Component Reference <a class="header-anchor" href="#particlesystem2d-component-reference" aria-label="Permalink to &quot;ParticleSystem2D Component Reference&quot;">​</a></h1><p>The ParticleSystem2D component is used to read the particle asset data and perform a series of operations such as play, pause, destroy, etc. Particle assets support <code>plist</code> files and images, both of which are recommended to be placed in the same folder.</p><p><img src="'+a+'" alt="ParticleSystem2D"></p><p>Click the <strong>Add Component</strong> button at the bottom of the <strong>Inspector</strong> panel and select <strong>ParticleSystem2D</strong> from <strong>Effects</strong> to add the ParticleSystem2D component to the node.</p><p>For the script interface of ParticleSystem2D, please refer to <a href="./__APIDOC__/en/class/ParticleSystem2D">ParticleSystem API</a>.</p><h2 id="particlesystem2d-properties" tabindex="-1">ParticleSystem2D Properties <a class="header-anchor" href="#particlesystem2d-properties" aria-label="Permalink to &quot;ParticleSystem2D Properties&quot;">​</a></h2><table><thead><tr><th style="text-align:left;">Property</th><th style="text-align:left;">Description</th></tr></thead><tbody><tr><td style="text-align:left;">CustomMaterial</td><td style="text-align:left;">Custom material, please refer to the <a href="./../../ui-system/components/engine/ui-material">Custom Material</a> documentation.</td></tr><tr><td style="text-align:left;">Color</td><td style="text-align:left;">Color of particles.</td></tr><tr><td style="text-align:left;">Preview</td><td style="text-align:left;">Previews particles in editor mode. When enabled, particles will be played automatically in the <strong>Scene</strong> panel when they are selected.</td></tr><tr><td style="text-align:left;">PlayOnLoad</td><td style="text-align:left;">If this option is checked, the particles will be emitted automatically at runtime.</td></tr><tr><td style="text-align:left;">AutoRemoveOnFinish</td><td style="text-align:left;">Automatically destroys the node where the particle is located when it finishes playing.</td></tr><tr><td style="text-align:left;">File</td><td style="text-align:left;">The particle configuration file in Plist format.</td></tr><tr><td style="text-align:left;">Custom</td><td style="text-align:left;">Whether to allow custom particle properties. When this property is enabled, the particle properties of the following sections can be customized.</td></tr><tr><td style="text-align:left;">SpriteFrame</td><td style="text-align:left;">Customizes the texture of particles.</td></tr><tr><td style="text-align:left;">Duration</td><td style="text-align:left;">The duration of the particle system in <strong>seconds</strong>, -1 means continuous emission.</td></tr><tr><td style="text-align:left;">EmissionRate</td><td style="text-align:left;">The number of particles emitted per second.</td></tr><tr><td style="text-align:left;">Life</td><td style="text-align:left;">The running time and range of particles.</td></tr><tr><td style="text-align:left;">TotalParticle</td><td style="text-align:left;">The maximum number of particles.</td></tr><tr><td style="text-align:left;">StartColor</td><td style="text-align:left;">The initial color of particles.</td></tr><tr><td style="text-align:left;">EndColor</td><td style="text-align:left;">The end color of particles.</td></tr><tr><td style="text-align:left;">Angle</td><td style="text-align:left;">The angle and range of particles.</td></tr><tr><td style="text-align:left;">StartSize</td><td style="text-align:left;">The initial size and range of particles.</td></tr><tr><td style="text-align:left;">EndSize</td><td style="text-align:left;">The end size and range of particles.</td></tr><tr><td style="text-align:left;">StartSpin</td><td style="text-align:left;">The initial spin angle and range of particles.</td></tr><tr><td style="text-align:left;">EndSpin</td><td style="text-align:left;">The end spin angle and size of particles.</td></tr><tr><td style="text-align:left;">PosVar</td><td style="text-align:left;">The range of the emitter position (horizontal and vertical).</td></tr><tr><td style="text-align:left;">PositionType</td><td style="text-align:left;">The type of particle position, including <strong>FREE</strong>, <strong>RELATIVE</strong>, <strong>GROUPED</strong>. For details, please refer to <a href="./__APIDOC__/en/class/ParticleSystem2D?id=positionType">PositionType API</a>.</td></tr><tr><td style="text-align:left;">EmitterMode</td><td style="text-align:left;">The type of the emitter, including <strong>GRAVITY</strong>, <strong>RADIUS</strong>. For details, please refer to <a href="./__APIDOC__/en/class/ParticleSystem2D?id=EmitterMode">EmitterMode API</a>.</td></tr><tr><td style="text-align:left;">Gravity</td><td style="text-align:left;">Gravity. Only works when Emitter Mode is set to <strong>GRAVITY</strong>.</td></tr><tr><td style="text-align:left;">Speed</td><td style="text-align:left;">The speed and range. Only effective when Emitter Mode is set to <strong>GRAVITY</strong>.</td></tr><tr><td style="text-align:left;">TangentialAccel</td><td style="text-align:left;">The tangential acceleration and range of each particle i.e. the acceleration perpendicular to the direction of gravity. Effective only when Emitter Mode is set to <strong>GRAVITY</strong>.</td></tr><tr><td style="text-align:left;">RadialAccel</td><td style="text-align:left;">The radial acceleration and range of particles, i.e. the acceleration parallel to the gravity direction. Effective only when Emitter Mode is set to <strong>GRAVITY</strong>.</td></tr><tr><td style="text-align:left;">RotationIsDir</td><td style="text-align:left;">Whether the rotation of each particle is equal to its direction. Effective only when Emitter Mode is set to <strong>GRAVITY</strong>.</td></tr><tr><td style="text-align:left;">StartRadius</td><td style="text-align:left;">The initial radius and range, which indicates the distance of particles relative to the emitter when it is launched. Effective only when Emitter Mode is set to <strong>RADIUS</strong>.</td></tr><tr><td style="text-align:left;">EndRadius</td><td style="text-align:left;">The end radius and range. Valid only when Emitter Mode is set to <strong>RADIUS</strong>.</td></tr><tr><td style="text-align:left;">RotatePerS</td><td style="text-align:left;">The rotation angle and range of particles around the initial point per second. Only effective when Emitter Mode is set to <strong>RADIUS</strong>.</td></tr></tbody></table><p>For more specific usage, please refer to the official <a href="https://github.com/cocos/cocos-test-projects/tree/v3.8/assets/cases/ui/25.particle" target="_blank" rel="noreferrer">ui/25.particle</a> example.</p>',8),n=[s];function o(d,c,f,g,p,y){return l(),e("div",null,n)}const x=t(i,[["render",o]]);export{m as __pageData,x as default};
