import{_ as t}from"./chunks/menu-button.CjNMAp13.js";import{_ as e,c as l,o as a,a4 as i}from"./chunks/framework.uQk9_EO2.js";const s="/docs/cocos/assets/main-module.BjS0G3DV.png",r="/docs/cocos/assets/set-pro.BrVY61PQ.png",n="/docs/cocos/assets/render-culling.FgVHl0ff.png",w=JSON.parse('{"title":"Main Module (Particle System)","description":"","frontmatter":{},"headers":[],"relativePath":"en/particle-system/main-module.md","filePath":"en/particle-system/main-module.md","lastUpdated":1712305443000}'),o={name:"en/particle-system/main-module.md"},d=i('<h1 id="main-module-particle-system" tabindex="-1">Main Module (Particle System) <a class="header-anchor" href="#main-module-particle-system" aria-label="Permalink to &quot;Main Module (Particle System)&quot;">​</a></h1><p>The particle system main module is used to store all data displayed in the <strong>Inspector</strong> panel, manage particle generation, playback, updates, and destroy related modules.</p><p><img src="'+s+'" alt="main-module"></p><table><thead><tr><th style="text-align:left;">Property</th><th style="text-align:left;">Description</th></tr></thead><tbody><tr><td style="text-align:left;">Duration</td><td style="text-align:left;">Total running time of the particle system.</td></tr><tr><td style="text-align:left;">Capacity</td><td style="text-align:left;">The maximum number of particles that the particle system can generate.</td></tr><tr><td style="text-align:left;">Loop</td><td style="text-align:left;">Whether the particle system loops.</td></tr><tr><td style="text-align:left;">PlayOnAwake</td><td style="text-align:left;">Whether the particle system automatically starts playing after loading.</td></tr><tr><td style="text-align:left;">Prewarm</td><td style="text-align:left;">After being selected, the particle system will start playing after one round has been played (only valid when loop playback is enabled).</td></tr><tr><td style="text-align:left;">SimulationSpace</td><td style="text-align:left;">The coordinate system where the particle coordinates are calculated.</td></tr><tr><td style="text-align:left;">SimulationSpeed</td><td style="text-align:left;">The update rate of the entire particle system.</td></tr><tr><td style="text-align:left;">StartDelay</td><td style="text-align:left;">The delayed emission time of the particles after the particle system starts running. (StartDelay will be set to 0 if Prewarm is selected)</td></tr><tr><td style="text-align:left;">StartLifetime</td><td style="text-align:left;">The life cycle of particles.</td></tr><tr><td style="text-align:left;">StartColor</td><td style="text-align:left;">The initial color of particles.</td></tr><tr><td style="text-align:left;">ScaleSpace</td><td style="text-align:left;">The coordinate system in which the particle is scaled<br><strong>Local</strong>: scaling based on the local coordinate system<br><strong>World</strong>: scaling based on the world coordinate system<br><strong>Custom</strong>: custom scaling, not affected by the node&#39;s <strong>scale</strong> property.</td></tr><tr><td style="text-align:left;">StartSize3D</td><td style="text-align:left;">The initial size of the X, Y, and Z axes of particles.</td></tr><tr><td style="text-align:left;">StartSize</td><td style="text-align:left;">The initial size of the X-axis of particles. Cannot be used with as <code>StartSize3D</code>.</td></tr><tr><td style="text-align:left;">StartSpeed</td><td style="text-align:left;">The initial velocity of particle.</td></tr><tr><td style="text-align:left;">StartRotation3D</td><td style="text-align:left;">The initial rotation angles of the X, Y, and Z axes of particles.</td></tr><tr><td style="text-align:left;">StartRotation</td><td style="text-align:left;">The initial rotation angle of particles.</td></tr><tr><td style="text-align:left;">GravityModifier</td><td style="text-align:left;">Gravity coefficient for particles affected by gravity (only CPU particles are supported).</td></tr><tr><td style="text-align:left;">RateOverTime</td><td style="text-align:left;">Number of particles emitted per second.</td></tr><tr><td style="text-align:left;">RateOverDistance</td><td style="text-align:left;">Number of particles emitted per moving unit distance.</td></tr><tr><td style="text-align:left;">Bursts</td><td style="text-align:left;">The number of Bursts that will emit the specified number of particles at the specified time. It can be adjusted by the following properties:<br><strong>Time</strong>: how long the particles play before the Burst starts to emit<br><strong>RepeatCount</strong>: number of Burst triggers<br><strong>RepeatInterval</strong>: time interval for each trigger<br><strong>Count</strong>: number of particles fired.</td></tr><tr><td style="text-align:left;">DataCulling</td><td style="text-align:left;">Particle system asset culling, please refer to the description below for details.</td></tr><tr><td style="text-align:left;">RenderCulling</td><td style="text-align:left;">Particle culling, please refer to the description below for details.</td></tr></tbody></table><p>Click the <img src="'+t+'" alt="menu button"> button to the right of the above property input box to open the particle curve/gradient editor and edit the particle properties, please refer to the <a href="./editor/">Particle Property Editor</a> documentation.</p><p><img src="'+r+'" alt="set-pro"></p><p>To use Particle System, please refer to the <a href="./__APIDOC__/en/class/ParticleSystem">Particle System API</a>.</p><h2 id="particle-system-asset-culling" tabindex="-1">Particle System Asset Culling <a class="header-anchor" href="#particle-system-asset-culling" aria-label="Permalink to &quot;Particle System Asset Culling&quot;">​</a></h2><p>The <strong>DataCulling</strong> option is used to cull the asset data of useless modules in the particle system.</p><p>Each module in the particle system exists as an independent object, and each module stores some module-related data, so the data recorded for modules that are not checked for use are useless data. When the developer does not need to dynamically open these unused modules at runtime, he can check the <strong>DataCulling</strong> option to cull these useless data and thus reduce asset usage.</p><blockquote><p><strong>Note</strong>: before v3.4 this option was <strong>EnableCulling</strong>, in v3.4 it was renamed to <strong>DataCulling</strong> to distinguish it from <strong>RenderCulling</strong> below. This adjustment has been done for compatibility, and it does not affect users in any way.</p></blockquote><h2 id="particle-culling" tabindex="-1">Particle Culling <a class="header-anchor" href="#particle-culling" aria-label="Permalink to &quot;Particle Culling&quot;">​</a></h2><p>Starting with v3.4, a new <strong>RenderCulling</strong> option has been added to the particle system to enable particle culling.</p><p>If this option is enabled, the particle emitter will automatically calculate a culling box, that will be used to cull the particle emitter at runtime depending on whether the culling box is within the visible range of the camera, or not. The culling operation will be performed every frame, which is suitable for some time-consuming effects, it is not recommended to turn this feature on if the number of particles is small.</p><p>The size of the bounding box can be adjusted by using the <strong>AabbHalf</strong> button in the picture below. Once the adjustment is complete, click the <strong>Regenerate bounding box</strong> button to recalculate the bounding box.</p><p><img src="'+n+'" alt="render culling"></p><table><thead><tr><th style="text-align:left;">Property</th><th style="text-align:left;">Description</th></tr></thead><tbody><tr><td style="text-align:left;">CullingMode</td><td style="text-align:left;">The behavior of the particle emitter after it has been culled. Possible options include <strong>Pause</strong>, <strong>Pause and Catchup</strong>, <strong>Always Simulate</strong>. <br><strong>Pause</strong>: if the particle emitter bounding box is not in the visible range of the camera, particles will pause the simulation. If it resumes visibility, particles will continue the simulation at the same time as the last pause;<br><strong>Pause and Catchup</strong>: if the particle emitter bounding box is not in the visible range of the camera, particles will pause the simulation. If it resumes visibility, particles will start simulating at the current time;<br><strong>Always Simulate</strong>: particles will always simulate whether or not the particle emitter box is in the camera&#39;s visible range, but it will not render when it is not in the camera&#39;s visible range.</td></tr><tr><td style="text-align:left;">AabbHalfX</td><td style="text-align:left;">The half-width of the particle emitter bounding box.</td></tr><tr><td style="text-align:left;">AabbHalfY</td><td style="text-align:left;">The half-height of the particle emitter bounding box.</td></tr><tr><td style="text-align:left;">AabbHalfZ</td><td style="text-align:left;">The half-length of the particle emitter bounding box.</td></tr><tr><td style="text-align:left;">Show Bounds</td><td style="text-align:left;">Shows the particle emitter bounding box in the <strong>Scene</strong> panel.</td></tr><tr><td style="text-align:left;">Regenerate bounding box</td><td style="text-align:left;">Click this button to recalculate the bounding box after it has been resized.</td></tr></tbody></table>',17),c=[d];function g(h,p,u,f,m,y){return a(),l("div",null,c)}const S=e(o,[["render",g]]);export{w as __pageData,S as default};
