import{_ as t}from"./chunks/menu-button.CjNMAp13.js";import{_ as e,c as n,o as i,a4 as r}from"./chunks/framework.uQk9_EO2.js";const a="/docs/cocos/assets/texture_animation.hbr8Op-N.gif",o="/docs/cocos/assets/texture_animation.Cg-tOupX.png",_=JSON.parse('{"title":"Texture Animation Module","description":"","frontmatter":{},"headers":[],"relativePath":"en/particle-system/texture-animation-module.md","filePath":"en/particle-system/texture-animation-module.md","lastUpdated":1712305443000}'),s={name:"en/particle-system/texture-animation-module.md"},l=r('<h1 id="texture-animation-module" tabindex="-1">Texture Animation Module <a class="header-anchor" href="#texture-animation-module" aria-label="Permalink to &quot;Texture Animation Module&quot;">​</a></h1><p>The texture animation module is used to animate the texture specified by the <strong>ParticleMaterial</strong> property in the <a href="./renderer">Particle Renderer</a> with the texture specified by the <strong>ParticleMaterial</strong> property as an animation frame to achieve an effect similar to the one in the following figure.</p><p><img src="'+a+'" alt="texture_animation"></p><h2 id="properties" tabindex="-1">Properties <a class="header-anchor" href="#properties" aria-label="Permalink to &quot;Properties&quot;">​</a></h2><p><img src="'+o+'" alt="texture_animation"></p><table><thead><tr><th style="text-align:left;">Properties</th><th style="text-align:left;">Description</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>Mode</strong></td><td style="text-align:left;">Set the type of particle animation mapping, currently only <strong>Grid</strong> (grid) mode is supported. A texture contains one animation frame for particle playback.</td></tr><tr><td style="text-align:left;"><strong>NumTilesX</strong></td><td style="text-align:left;">The number of maps divided by the texture in the horizontal (X) direction.</td></tr><tr><td style="text-align:left;"><strong>NumTilesY</strong></td><td style="text-align:left;">The number of maps in the vertical (Y) direction of the texture.</td></tr><tr><td style="text-align:left;"><strong>Animation</strong></td><td style="text-align:left;">Animation playback methods, including:<br> <strong>WholeSheet</strong>: play all frames in the mapping;<br> <strong>SingleRow</strong>: play only one of the rows, the first row by default. Can be used with <strong>RandomRow</strong> and <strong>RowIndex</strong> properties.</td></tr><tr><td style="text-align:left;"><strong>RandomRow</strong></td><td style="text-align:left;">Select a random row from the animation map to play the animation frames.<br>This item only takes effect when <strong>Animation</strong> is set to <strong>SingleRow</strong>.</td></tr><tr><td style="text-align:left;"><strong>RowIndex</strong></td><td style="text-align:left;">Select a specific row from the animation map to play the animation frames.<br>This item only takes effect when <strong>Animation</strong> is set to <strong>SingleRow</strong> and <strong>RandomRow</strong> is disabled.</td></tr><tr><td style="text-align:left;"><strong>FrameOverTime</strong></td><td style="text-align:left;">Set the animation playback speed.<br>When clicking the <img src="'+t+'" alt="menu button"> button to the right of the input box to switch to using curve editing, it indicates the frame vs. time change curve of the animation playing in one cycle.</td></tr><tr><td style="text-align:left;"><strong>StartFrame</strong></td><td style="text-align:left;">Specifies the frame at which the animation starts to play throughout the life of the particle system.</td></tr><tr><td style="text-align:left;"><strong>CycleCount</strong></td><td style="text-align:left;">The number of times the animation frame will be repeated during the particle lifetime.</td></tr></tbody></table>',6),d=[l];function m(g,h,c,p,f,u){return i(),n("div",null,d)}const b=e(s,[["render",m]]);export{_ as __pageData,b as default};
