import{_ as e,c as t,o,a4 as r}from"./chunks/framework.uQk9_EO2.js";const m=JSON.parse('{"title":"UICoordinateTracker component reference","description":"","frontmatter":{},"headers":[],"relativePath":"en/ui-system/components/editor/ui-coordinate-tracker.md","filePath":"en/ui-system/components/editor/ui-coordinate-tracker.md","lastUpdated":1712305443000}'),n={name:"en/ui-system/components/editor/ui-coordinate-tracker.md"},a=r('<h1 id="uicoordinatetracker-component-reference" tabindex="-1">UICoordinateTracker component reference <a class="header-anchor" href="#uicoordinatetracker-component-reference" aria-label="Permalink to &quot;UICoordinateTracker component reference&quot;">​</a></h1><p>The UICoordinateTracker component performs coordinate conversion on the UI and simulates the foreshortening effects of 3D objects in the perspective camera. Return the converted coordinates and the proportion of the object in the viewport through events. Suitable for functions such as 3D character blood bars and name bars.</p><p>Click the <strong>Add Component</strong> button at the bottom of the <strong>Inspector</strong> panel and select <strong>UICoordinateTracker</strong> from <strong>UI</strong> to add the <strong>UICoordinateTracker</strong> component to the node.</p><h2 id="uicoordinatetracker-properties" tabindex="-1">UICoordinateTracker properties <a class="header-anchor" href="#uicoordinatetracker-properties" aria-label="Permalink to &quot;UICoordinateTracker properties&quot;">​</a></h2><table><thead><tr><th style="text-align:left;"><strong>Property</strong></th><th style="text-align:left;"><strong>Function Description</strong></th></tr></thead><tbody><tr><td style="text-align:left;"><strong>Target</strong></td><td style="text-align:left;">Target object. Which UI node needs to be converted to.</td></tr><tr><td style="text-align:left;"><strong>Camera</strong></td><td style="text-align:left;">The 3D camera representing the original coordinate system.</td></tr><tr><td style="text-align:left;"><strong>UseScale</strong></td><td style="text-align:left;">Whether to enable scaling, needs to be used in combination with the <code>Distance</code> property. If enabled, the scaling of the mapped object in the perspective camera will be adjusted according to the distance between the 3D node coordinates and the camera, resulting in a foreshortening effects.</td></tr><tr><td style="text-align:left;"><strong>Distance</strong></td><td style="text-align:left;">The distance from the camera is calculated for normal display. Adjust the best position according to the illumination effect of the model under the camera, and use this position as the dividing line to calculate the proportion in the viewport.</td></tr><tr><td style="text-align:left;"><strong>SyncEvents</strong></td><td style="text-align:left;">Map data events. The first parameter of the callback is the local coordinates after mapping, and the second is the distance ratio from the camera.</td></tr></tbody></table><p>For specific usage, please refer to the <strong>rocker</strong> scene in the <a href="https://github.com/cocos/cocos-example-ui/tree/v3.0/assets/scene" target="_blank" rel="noreferrer">UI display example</a>.</p>',6),s=[a];function i(c,d,l,h,p,g){return o(),t("div",null,s)}const u=e(n,[["render",i]]);export{m as __pageData,u as default};
