import{_ as s,c as t,o,a5 as r}from"./chunks/framework.CqnjdYks.js";const a="/docs/cocos/assets/embedded-overview.DYhE5w9Q.png",e="/docs/cocos/assets/enable.CFXJRg0r.png",n="/docs/cocos/assets/add-track.V5sB5S9b.png",c="/docs/cocos/assets/clear-data.9P8Eg5J3.png",p="/docs/cocos/assets/track-data-menu.CIqhosfN.png",i="/docs/cocos/assets/unselect.CR267nJ-.png",l="/docs/cocos/assets/track-adjust.DgVyD9oz.gif",g="/docs/cocos/assets/particle-inspector.CNOVCs3U.png",d="/docs/cocos/assets/inspector-select-child.B03OJnW3.png",m="/docs/cocos/assets/inspector-select-anim.dCm1Wk4y.png",_="/docs/cocos/assets/inspector-select-child-anim.BAy25dae.png",h="/docs/cocos/assets/inspector-select-clip.ByCyRzDw.png",u="/docs/cocos/assets/preview.B8gUqHCb.gif",b="/docs/cocos/assets/add-keyframe-event.CRoWdL9T.png",D=JSON.parse('{"title":"嵌入播放器（实验性功能）","description":"","frontmatter":{},"headers":[],"relativePath":"zh/manual/animation/embedded-player.md","filePath":"zh/manual/animation/embedded-player.md"}'),f={name:"zh/manual/animation/embedded-player.md"},k=r('<h1 id="嵌入播放器-实验性功能" tabindex="-1">嵌入播放器（实验性功能） <a class="header-anchor" href="#嵌入播放器-实验性功能" aria-label="Permalink to &quot;嵌入播放器（实验性功能）&quot;">​</a></h1><p>嵌入播放器的作用是允许在动画剪辑的制作时，可以同时绑定粒子特效或其他动画等，用于满足在播放动画同时播放动效的功能。可以用于实现挥动武器会出现刀光，或者脚踩在地面上会出现烟尘等功能。</p><p>在动画窗口展开 <strong>播放器轨道列表</strong> 即可观察整个嵌套系统：</p><p><img src="'+a+'" alt="overview"></p><blockquote><p><strong>注意</strong>：在 v3.6 中，该功能为实验性功能，开发者需要在 <strong>偏好设置</strong> -&gt; <strong>实验室中</strong> 启用。<br> 有任何的使用体验问题或者建议，欢迎在 <a href="https://forum.cocos.org/t/topic/137740" target="_blank" rel="noreferrer">论坛</a> 反馈给我们。</p><p><img src="'+e+'" alt="enable"></p></blockquote><h2 id="轨道操作" tabindex="-1">轨道操作 <a class="header-anchor" href="#轨道操作" aria-label="Permalink to &quot;轨道操作&quot;">​</a></h2><p>嵌入播放器支持 <strong>动画播放器</strong> 以及 <strong>粒子播放器</strong>。</p><p>在 <strong>动画轨道列表</strong> 的右侧点击 <strong>+</strong> 按钮即可添加不同的轨道：</p><p><img src="'+n+'" alt="track"></p><p>在添加后的轨道上点击鼠标右键可以 <strong>移除播放器轨道</strong> 和 <strong>清除数据</strong>。</p><p><img src="'+c+'" alt="delete"></p><blockquote><p><strong>注意</strong>：清空数据操作会清空整个轨道上的内容。</p></blockquote><p>在轨道数据上点击鼠标右键可以下选择 <strong>复制</strong> 和 <strong>删除</strong> 操作。</p><p>删除操作仅删除当前选中的数据。</p><p><img src="'+p+'" alt="menu"></p><p>未选中任何数据时，可以选择 <strong>创建</strong>、<strong>粘贴</strong> 和 <strong>清除数据</strong>。</p><p><img src="'+i+'" alt="unselect"></p><p>添加轨道完成后，可以调整轨道数据的位置以及时长：</p><p><img src="'+l+'" alt="duration"></p><h3 id="粒子播放器轨道" tabindex="-1">粒子播放器轨道 <a class="header-anchor" href="#粒子播放器轨道" aria-label="Permalink to &quot;粒子播放器轨道&quot;">​</a></h3><p>在 <strong>动画轨道列表</strong> 中选中左侧已添加的粒子播放器轨道，在 <strong>属性检查器</strong> 可以调整轨道的属性。</p><p><img src="'+g+'" alt="inspector"></p><ul><li><p><strong>同步速度</strong>：使嵌入粒子系统与当前动画剪辑保持一致的播放速率</p></li><li><p><strong>子节点</strong>：通过下拉框可以选择不同的子节点上的粒子系统：</p><p><img src="'+d+'" alt="select child"></p></li></ul><h3 id="动画播放器轨道" tabindex="-1">动画播放器轨道 <a class="header-anchor" href="#动画播放器轨道" aria-label="Permalink to &quot;动画播放器轨道&quot;">​</a></h3><p>在 <strong>动画轨道列表</strong> 中选中左侧已添加的动画播放器轨道，在 <strong>属性检查器</strong> 可以调整轨道的属性。</p><p><img src="'+m+'" alt="anim"></p><ul><li><p><strong>同步速度</strong>：使嵌入动画与当前的动画剪辑保持一致的播放速率</p></li><li><p><strong>子节点</strong>：通过下拉框可以选择不同的子节点上的动画组件：</p><p><img src="'+_+'" alt="anim"></p></li><li><p><strong>动画剪辑</strong>：通过下拉菜单可以选择不同的动画剪辑：</p><p><img src="'+h+'" alt="clip"></p></li></ul><h2 id="预览" tabindex="-1">预览 <a class="header-anchor" href="#预览" aria-label="Permalink to &quot;预览&quot;">​</a></h2><p>通过上述操作即可在动画播放时嵌套不同的粒子播放器和动画播放器，展示效果如下：</p><p><img src="'+u+'" alt="preview"></p><h2 id="添加帧事件" tabindex="-1">添加帧事件 <a class="header-anchor" href="#添加帧事件" aria-label="Permalink to &quot;添加帧事件&quot;">​</a></h2><p>在嵌套栏点击鼠标右键也可以添加帧事件，其使用方式和 <a href="./animation-event.html">添加动画事件</a> 一致。</p><p><img src="'+b+'" alt="evt"></p>',33),q=[k];function v(P,C,y,x,B,T){return o(),t("div",null,q)}const S=s(f,[["render",v]]);export{D as __pageData,S as default};
