import{_ as t,v as s,b as a,R as n}from"./chunks/framework.5ffcbaff.js";import{_ as l}from"./chunks/animation-controller.72404bab.js";const o="/docs/cocos/assets/add-animation-controller.fe9e229f.png",m=JSON.parse('{"title":"动画控制器（Animation Controller）组件参考","description":"","frontmatter":{},"headers":[],"relativePath":"zh/manual/animation/marionette/animation-controller.md","filePath":"zh/manual/animation/marionette/animation-controller.md"}'),e={name:"zh/manual/animation/marionette/animation-controller.md"},p=n('<h1 id="动画控制器-animation-controller-组件参考" tabindex="-1">动画控制器（Animation Controller）组件参考 <a class="header-anchor" href="#动画控制器-animation-controller-组件参考" aria-label="Permalink to &quot;动画控制器（Animation Controller）组件参考&quot;">​</a></h1><p>动画控制器组件用于将 <a href="./animation-graph.html">动画图资源</a> 应用到对象上。</p><p>在 <strong>层级管理器</strong> 中选中需要应用动画图的节点，然后在 <strong>属性检查器</strong> 中点击下方的 <strong>添加组件 -&gt; Animation -&gt; Animation Controller</strong>，即可添加一个动画控制器组件到节点上。</p><p><img src="'+o+'" alt="add-animation-controller"></p><blockquote><p><strong>注意</strong>：因为 Marionette 动画系统与旧式动画系统不可同时使用，因此 <strong>动画控制器组件也不能和动画组件或者骨骼动画组件挂载在同一个节点上</strong>。</p></blockquote><h2 id="属性" tabindex="-1">属性 <a class="header-anchor" href="#属性" aria-label="Permalink to &quot;属性&quot;">​</a></h2><p><img src="'+l+`" alt="animation-controller"></p><table><thead><tr><th style="text-align:left;">属性</th><th style="text-align:left;">说明</th></tr></thead><tbody><tr><td style="text-align:left;">Graph</td><td style="text-align:left;">添加的 <a href="./animation-graph.html">动画图资源</a>，默认为空</td></tr></tbody></table><h2 id="程序化控制动画图" tabindex="-1">程序化控制动画图 <a class="header-anchor" href="#程序化控制动画图" aria-label="Permalink to &quot;程序化控制动画图&quot;">​</a></h2><p>动画控制器组件提供了一些方法用于在运行时控制动画图的 <a href="./animation-graph-basics.html">动画状态机</a>：</p><table><thead><tr><th style="text-align:left;">方法</th><th style="text-align:left;">说明</th></tr></thead><tbody><tr><td style="text-align:left;"><code>getVariables</code></td><td style="text-align:left;">获取所有的变量及变量名/类型/值等相关信息。</td></tr><tr><td style="text-align:left;"><code>setValue</code></td><td style="text-align:left;">设置变量的值。</td></tr><tr><td style="text-align:left;"><code>getValue</code></td><td style="text-align:left;">获取变量的值。</td></tr><tr><td style="text-align:left;"><code>getCurrentStateStatus</code></td><td style="text-align:left;">获取当前状态的信息。</td></tr><tr><td style="text-align:left;"><code>getCurrentClipStatuses</code></td><td style="text-align:left;">获取当前的动画剪辑的状态。</td></tr><tr><td style="text-align:left;"><code>getCurrentTransition</code></td><td style="text-align:left;">获取当前的状态过渡。</td></tr><tr><td style="text-align:left;"><code>getNextStateStatus</code></td><td style="text-align:left;">获取下一个状态的信息。</td></tr><tr><td style="text-align:left;"><code>getNextClipStatuses</code></td><td style="text-align:left;">获取下一个动画剪辑的状态。</td></tr><tr><td style="text-align:left;"><code>setLayerWeight</code></td><td style="text-align:left;">设置某个 <a href="./animation-graph-layer.html">动画图层级</a> 的权重。</td></tr></tbody></table><ul><li><p><strong>变量</strong> 控制</p><p>例如我们在 <a href="./animation-graph-panel.html#变量（variables）">动画图面板</a> 添加了多个变量，通过代码可以获取和修改变量的值，代码示例如下：</p><div class="language-ts"><button title="Copy Code" class="copy"></button><span class="lang">ts</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#676E95;font-style:italic;">// 获取动画控制器组件    </span></span>
<span class="line"><span style="color:#C792EA;">let</span><span style="color:#A6ACCD;"> animationController</span><span style="color:#89DDFF;">:</span><span style="color:#FFCB6B;">animation</span><span style="color:#89DDFF;">.</span><span style="color:#FFCB6B;">AnimationController</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">this.</span><span style="color:#A6ACCD;">node</span><span style="color:#89DDFF;">.</span><span style="color:#82AAFF;">getComponent</span><span style="color:#A6ACCD;">(animation</span><span style="color:#89DDFF;">.</span><span style="color:#A6ACCD;">AnimationController)</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;font-style:italic;">// 获取所有的变量</span></span>
<span class="line"><span style="color:#C792EA;">let</span><span style="color:#A6ACCD;"> variables</span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> animationController</span><span style="color:#89DDFF;">.</span><span style="color:#82AAFF;">getVariables</span><span style="color:#A6ACCD;">()</span><span style="color:#89DDFF;">;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;font-style:italic;">// 获取名为 ‘vertical’ 的变量的值</span></span>
<span class="line"><span style="color:#C792EA;">let</span><span style="color:#A6ACCD;"> vertical</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">Number</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> animationController</span><span style="color:#89DDFF;">.</span><span style="color:#82AAFF;">getValue</span><span style="color:#A6ACCD;">(</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">vertical</span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;">)</span><span style="color:#89DDFF;">;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;font-style:italic;">// 修改名为 ‘vertical’ 的变量的值为 1.0。修改后，将会在下一帧 update 的时候计算条件过渡</span></span>
<span class="line"><span style="color:#A6ACCD;">animationController</span><span style="color:#89DDFF;">.</span><span style="color:#82AAFF;">setValue</span><span style="color:#A6ACCD;">(</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">vertical</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">,</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">1.0</span><span style="color:#A6ACCD;">)</span></span></code></pre></div><blockquote><p><strong>注意</strong>：动画状态机的变量目前只能在 <a href="./animation-graph-panel.html#变量（variables）">动画图面板</a> 进行添加和删除。</p></blockquote></li><li><p>获取当前 <strong>状态</strong></p><p>获取第 0 层级状态机当前 <strong>状态</strong> 的归一化进度，代码示例如下：</p><div class="language-ts"><button title="Copy Code" class="copy"></button><span class="lang">ts</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#C792EA;">let</span><span style="color:#A6ACCD;"> states</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">animation</span><span style="color:#89DDFF;">.</span><span style="color:#FFCB6B;">MotionStateStatus</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> animationController</span><span style="color:#89DDFF;">.</span><span style="color:#82AAFF;">getCurrentStateStatus</span><span style="color:#A6ACCD;">(</span><span style="color:#F78C6C;">0</span><span style="color:#A6ACCD;">)</span></span>
<span class="line"><span style="color:#A6ACCD;">console</span><span style="color:#89DDFF;">.</span><span style="color:#82AAFF;">log</span><span style="color:#A6ACCD;">(states</span><span style="color:#89DDFF;">.</span><span style="color:#A6ACCD;">progress)</span><span style="color:#89DDFF;">;</span></span></code></pre></div></li><li><p>获取当前 <strong>过渡</strong></p><p>获取第 0 层级状态机即将发生的 <strong>过渡</strong> 的相关信息：</p><div class="language-ts"><button title="Copy Code" class="copy"></button><span class="lang">ts</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#C792EA;">let</span><span style="color:#A6ACCD;"> transition</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">animation</span><span style="color:#89DDFF;">.</span><span style="color:#FFCB6B;">TransitionStatus</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> animationController</span><span style="color:#89DDFF;">.</span><span style="color:#82AAFF;">getCurrentTransition</span><span style="color:#A6ACCD;">(</span><span style="color:#F78C6C;">0</span><span style="color:#A6ACCD;">)</span></span>
<span class="line"><span style="color:#A6ACCD;">console</span><span style="color:#89DDFF;">.</span><span style="color:#82AAFF;">log</span><span style="color:#A6ACCD;">(transition</span><span style="color:#89DDFF;">.</span><span style="color:#A6ACCD;">duration</span><span style="color:#89DDFF;">,</span><span style="color:#A6ACCD;"> transition</span><span style="color:#89DDFF;">.</span><span style="color:#A6ACCD;">time)</span></span></code></pre></div></li></ul>`,12),r=[p];function i(c,y,C,d,D,A){return s(),a("div",null,r)}const h=t(e,[["render",i]]);export{m as __pageData,h as default};
