import{_ as t,v as e,b as l,R as a}from"./chunks/framework.5ffcbaff.js";const s="/docs/cocos/assets/layer-gizmo.31fd502c.png",r="/docs/cocos/assets/layer-edit.3bb2bcb2.png",m=JSON.parse('{"title":"层级","description":"","frontmatter":{},"headers":[],"relativePath":"zh/manual/concepts/scene/layer.md","filePath":"zh/manual/concepts/scene/layer.md"}'),n={name:"zh/manual/concepts/scene/layer.md"},d=a('<h1 id="层级" tabindex="-1">层级 <a class="header-anchor" href="#层级" aria-label="Permalink to &quot;层级&quot;">​</a></h1><p>节点的 Layer 属性是一个无符号 32 位的整数，最多支持 32 个不同类型的 Layer，可在编辑器上方菜单栏的 <strong>项目 -&gt; 项目设置 -&gt; <a href="./../../editor/project/#layers">Layers</a></strong> 中设置。其中开发者可自定义第 0 ~ 19 个 Layer，剩下后面的 12 个 Layers 则是引擎内置的。</p><p>相机的 Visibility 属性跟节点的 Layer 属性，都是用来控制其可见性的。但只有当节点设置的 Layer 属性包含在相机的 Visibility 中时，节点才可以被相机看见。相机的 Visibility 属性采用 <code>|</code> 和 <code>&amp;</code> 这种位操作符来判断节点的 Layer 是否应该被观察到，且支持同时选择多个 Layer。具体内容可参考 <a href="./../../editor/components/camera-component.html#相机分组渲染">Camera — Visibility 属性</a>。</p><h2 id="引擎内置的层级" tabindex="-1">引擎内置的层级 <a class="header-anchor" href="#引擎内置的层级" aria-label="Permalink to &quot;引擎内置的层级&quot;">​</a></h2><p><img src="'+s+'" alt="layer gizmo"></p><table><thead><tr><th style="text-align:left;">属性</th><th style="text-align:left;">说明</th><th style="text-align:left;">值</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>NONE</strong></td><td style="text-align:left;">设置全都不可见</td><td style="text-align:left;">0</td></tr><tr><td style="text-align:left;"><strong>IGNORE_RAYCAST</strong></td><td style="text-align:left;">设置忽略射线检测</td><td style="text-align:left;">1 &lt;&lt; 20</td></tr><tr><td style="text-align:left;"><strong>GIZMOS</strong></td><td style="text-align:left;">设置配置信息可见</td><td style="text-align:left;">1 &lt;&lt; 21</td></tr><tr><td style="text-align:left;"><strong>EDITOR</strong></td><td style="text-align:left;">设置编辑器可见</td><td style="text-align:left;">1 &lt;&lt; 22</td></tr><tr><td style="text-align:left;"><strong>UI_3D</strong></td><td style="text-align:left;">设置 3D <strong>UI</strong> 节点可见</td><td style="text-align:left;">1 &lt;&lt; 23</td></tr><tr><td style="text-align:left;"><strong>SCENE_GIZMO</strong></td><td style="text-align:left;">设置场景配置节点可见</td><td style="text-align:left;">1 &lt;&lt; 24</td></tr><tr><td style="text-align:left;"><strong>UI_2D</strong></td><td style="text-align:left;">设置 2D <strong>UI</strong> 节点可见</td><td style="text-align:left;">1 &lt;&lt; 25</td></tr><tr><td style="text-align:left;"><strong>PROFILER</strong></td><td style="text-align:left;">设置分析工具节点可见</td><td style="text-align:left;">1 &lt;&lt; 28</td></tr><tr><td style="text-align:left;"><strong>DEFAULT</strong></td><td style="text-align:left;">设置默认节点可见</td><td style="text-align:left;">1 &lt;&lt; 30</td></tr><tr><td style="text-align:left;"><strong>ALL</strong></td><td style="text-align:left;">设置所有节点可见</td><td style="text-align:left;">0xffffffff</td></tr></tbody></table><h2 id="用户自定义层级" tabindex="-1">用户自定义层级 <a class="header-anchor" href="#用户自定义层级" aria-label="Permalink to &quot;用户自定义层级&quot;">​</a></h2><p><img src="'+r+'" alt="layer gizmo"></p>',8),o=[d];function i(g,y,f,c,x,h){return e(),l("div",null,o)}const _=t(n,[["render",i]]);export{m as __pageData,_ as default};
