import{_ as t,v as e,b as s,R as l}from"./chunks/framework.5ffcbaff.js";const r="/docs/cocos/assets/sphere-light.47dad50a.jpg",a="/docs/cocos/assets/sphere-light-edit.988926d0.png",o="/docs/cocos/assets/sphere-light-prop.c9cc0bab.png",y=JSON.parse('{"title":"球面光","description":"","frontmatter":{},"headers":[],"relativePath":"zh/manual/concepts/scene/light/lightType/sphere-light.md","filePath":"zh/manual/concepts/scene/light/lightType/sphere-light.md"}'),n={name:"zh/manual/concepts/scene/light/lightType/sphere-light.md"},i=l('<h1 id="球面光" tabindex="-1">球面光 <a class="header-anchor" href="#球面光" aria-label="Permalink to &quot;球面光&quot;">​</a></h1><p>Cocos Creator 3.x 的球面光与 v2.x 的点光源（Point Light）类似。</p><p>球面光会向所有方向均匀地发散光线，接近于蜡烛产生的光线。物体受到的光照强度会随着跟光源距离的增大而减弱，当距离超过设置的光照影响范围，则光照强度为 0。</p><p>在实际应用中可用于模拟火把、蜡烛、灯泡等光源，照亮四周一定距离内的环境。</p><p><img src="'+r+'" alt="sphere-light-edit"></p><p>在编辑器中可以直观地看到光源位置、颜色，以及它的照射范围，如下图所示。通过修改 <strong>属性检查器</strong> 中球面光组件的 <code>Range</code> 属性即可调整球面光的光照范围。</p><p><img src="'+a+'" alt="sphere-light-edit"></p><p>在场景中添加球面光的方式可参考 <a href="./">添加光源</a>。</p><p>球面光组件接口请参考 <a href="./__APIDOC__/zh/class/SphereLight.html">SphereLight API</a>。</p><h2 id="球面光属性" tabindex="-1">球面光属性 <a class="header-anchor" href="#球面光属性" aria-label="Permalink to &quot;球面光属性&quot;">​</a></h2><p><img src="'+o+'" alt="image"></p><table><thead><tr><th style="text-align:left;">属性</th><th style="text-align:left;">说明</th></tr></thead><tbody><tr><td style="text-align:left;">Color</td><td style="text-align:left;">设置光源颜色</td></tr><tr><td style="text-align:left;">UseColorTemperature</td><td style="text-align:left;">是否启用色温</td></tr><tr><td style="text-align:left;">ColorTemperature</td><td style="text-align:left;">调节色温</td></tr><tr><td style="text-align:left;">Size</td><td style="text-align:left;">设置光源大小</td></tr><tr><td style="text-align:left;">Range</td><td style="text-align:left;">设置光照影响范围</td></tr><tr><td style="text-align:left;">Term</td><td style="text-align:left;">设置光照强度单位的类型，包括 <strong>光通量（LUMINOUS_POWER</strong>）和 <strong>亮度（LUMINANCE）</strong> 两种</td></tr><tr><td style="text-align:left;">LuminousPower</td><td style="text-align:left;">光通量，单位 <strong>流明（lm）</strong><br>当 <strong>Term</strong> 设置为 <strong>LUMINOUS_POWER</strong> 时生效</td></tr><tr><td style="text-align:left;">Luminance</td><td style="text-align:left;">亮度，单位 <strong>坎德拉每平方米（cd/m<sup>2</sup>）</strong><br>当 <strong>Term</strong> 设置为 <strong>LUMINANCE</strong> 时生效</td></tr><tr><td style="text-align:left;">StaticSettings</td><td style="text-align:left;">静态灯光设置，详情请参考 <a href="./../lightmap.html">光照贴图</a></td></tr></tbody></table><blockquote><p><strong>注意</strong>：目前球面光的 <code>Size</code> 属性在实际运行中不生效，以及暂不支持显示阴影，我们会在后续版本进行优化，请关注版本更新公告。</p></blockquote>',13),d=[i];function g(h,p,c,f,m,x){return e(),s("div",null,d)}const u=t(n,[["render",g]]);export{y as __pageData,u as default};
