import{_ as t,c as e,o,a5 as a}from"./chunks/framework.CqnjdYks.js";const r="/docs/cocos/assets/motionstreak.utMbKRLl.png",s="/docs/cocos/assets/add-motion-streak.BDKEofKk.png",k=JSON.parse('{"title":"MotionStreak（拖尾）组件参考","description":"","frontmatter":{},"headers":[],"relativePath":"zh/manual/editor/components/motion-streak.md","filePath":"zh/manual/editor/components/motion-streak.md"}'),n={name:"zh/manual/editor/components/motion-streak.md"},l=a('<h1 id="motionstreak-拖尾-组件参考" tabindex="-1">MotionStreak（拖尾）组件参考 <a class="header-anchor" href="#motionstreak-拖尾-组件参考" aria-label="Permalink to &quot;MotionStreak（拖尾）组件参考&quot;">​</a></h1><p>MotionStreak（拖尾）是运动轨迹，用于在游戏对象的运动轨迹上实现拖尾渐隐效果。</p><p><img src="'+r+'" alt="motionstreak"></p><p>点击 <strong>属性检查器</strong> 下方的 <strong>添加组件</strong> 按钮，然后从 <strong>Effects</strong> 中选择 <strong>MotionStreak</strong>，即可添加 MotionStreak 组件到节点上。</p><p>具体的使用方式可以参考 <strong>MotionStreak</strong> (<a href="https://github.com/cocos/cocos-test-projects/tree/v3.7/assets/cases/ui/24.motion-streak" target="_blank" rel="noreferrer">GitHub</a> | <a href="https://gitee.com/mirrors_cocos-creator/test-cases-3d/tree/v3.7/assets/cases/ui/24.motion-streak" target="_blank" rel="noreferrer">Gitee</a>) 范例。</p><p><img src="'+s+'" alt="add motionStreak"></p><p>拖尾的脚本接口请参考 <a href="./__APIDOC__/zh/class/MotionStreak.html">MotionStreak API</a>。</p><h2 id="motionstreak-属性" tabindex="-1">MotionStreak 属性 <a class="header-anchor" href="#motionstreak-属性" aria-label="Permalink to &quot;MotionStreak 属性&quot;">​</a></h2><table><thead><tr><th style="text-align:left;">属性</th><th style="text-align:left;">功能说明</th></tr></thead><tbody><tr><td style="text-align:left;">CustomMaterial</td><td style="text-align:left;">自定义材质，使用方法请参考 <a href="./../../ui-system/components/engine/ui-material.html">自定义材质</a>。</td></tr><tr><td style="text-align:left;">Preview</td><td style="text-align:left;">是否启用预览。若勾选该项，则可在 <strong>场景编辑器</strong> 中预览拖尾效果</td></tr><tr><td style="text-align:left;">FadeTime</td><td style="text-align:left;">拖尾的渐隐时间，以秒为单位。</td></tr><tr><td style="text-align:left;">MinSeg</td><td style="text-align:left;">最小的片段长度（渐隐片段的大小）。拖尾条带相连顶点间的最小距离。</td></tr><tr><td style="text-align:left;">Stroke</td><td style="text-align:left;">拖尾的宽度。</td></tr><tr><td style="text-align:left;">Texture</td><td style="text-align:left;">拖尾的贴图。</td></tr><tr><td style="text-align:left;">FastMode</td><td style="text-align:left;">是否启用快速模式。当启用快速模式，新的顶点会被更快地添加，但精度较低。</td></tr><tr><td style="text-align:left;">Color</td><td style="text-align:left;">拖尾的颜色。</td></tr></tbody></table>',9),i=[l];function d(c,m,g,_,f,h){return o(),e("div",null,i)}const x=t(n,[["render",d]]);export{k as __pageData,x as default};
