import{_ as l,v as t,b as e,R as a}from"./chunks/framework.5ffcbaff.js";const s="data:image/png;base64,",i="/docs/cocos/assets/add_tiledtile.9576818c.png",h=JSON.parse('{"title":"TiledTile 组件参考","description":"","frontmatter":{},"headers":[],"relativePath":"zh/manual/editor/components/tiledtile.md","filePath":"zh/manual/editor/components/tiledtile.md"}'),n={name:"zh/manual/editor/components/tiledtile.md"},o=a('<h1 id="tiledtile-组件参考" tabindex="-1">TiledTile 组件参考 <a class="header-anchor" href="#tiledtile-组件参考" aria-label="Permalink to &quot;TiledTile 组件参考&quot;">​</a></h1><p>TiledTile 组件可以单独对某一个地图块进行操作。</p><p><img src="'+s+'" alt="tiledTile-component"></p><h2 id="创建方式" tabindex="-1">创建方式 <a class="header-anchor" href="#创建方式" aria-label="Permalink to &quot;创建方式&quot;">​</a></h2><h3 id="通过编辑器创建" tabindex="-1">通过编辑器创建 <a class="header-anchor" href="#通过编辑器创建" aria-label="Permalink to &quot;通过编辑器创建&quot;">​</a></h3><p>在创建 <a href="./tiledmap.html">TiledMap 组件</a> 过程中 <strong>自动生成</strong> 的 Layer 节点下创建一个空节点。然后选中该空节点，点击 <strong>属性检查器</strong> 下方的 <strong>添加组件 -&gt; TiledMap -&gt; TiledTile</strong>，即可添加 TiledTile 组件到节点上。再通过设置 TiledTile 组件上的属性来操作地图块。</p><p><img src="'+i+`" alt="add tiledTile"></p><p>相关 TiledTile 脚本接口请参考 <a href="./__APIDOC__/zh/class/TiledTile.html">TiledTile API</a></p><h3 id="通过代码创建" tabindex="-1">通过代码创建 <a class="header-anchor" href="#通过代码创建" aria-label="Permalink to &quot;通过代码创建&quot;">​</a></h3><p>在代码中设置地图块有两种方式。当你在某个 Layer 节点中设置了 TiledTile 之后，该 Layer 节点原先所在位置的 TiledTile 将会被取代。</p><h4 id="通过对一个节点添加-tiledtile-组件创建" tabindex="-1">通过对一个节点添加 TiledTile 组件创建 <a class="header-anchor" href="#通过对一个节点添加-tiledtile-组件创建" aria-label="Permalink to &quot;通过对一个节点添加 TiledTile 组件创建&quot;">​</a></h4><div class="language-ts"><button title="Copy Code" class="copy"></button><span class="lang">ts</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#676E95;font-style:italic;">// 创建一个新节点</span></span>
<span class="line"><span style="color:#C792EA;">const</span><span style="color:#A6ACCD;"> node </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">new</span><span style="color:#A6ACCD;"> </span><span style="color:#82AAFF;">Node</span><span style="color:#A6ACCD;">()</span><span style="color:#89DDFF;">;</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;">// 然后把该节点的父节点设置为任意的 layer 节点</span></span>
<span class="line"><span style="color:#A6ACCD;">node</span><span style="color:#89DDFF;">.</span><span style="color:#A6ACCD;">parent </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">this.</span><span style="color:#A6ACCD;">layer</span><span style="color:#89DDFF;">.</span><span style="color:#A6ACCD;">node</span><span style="color:#89DDFF;">;</span><span style="color:#A6ACCD;">  </span></span>
<span class="line"><span style="color:#676E95;font-style:italic;">// 最后添加 TiledTile 组件到该节点上，并返回 TiledTile 对象，就可以对 TiledTile 对象进行一系列操作</span></span>
<span class="line"><span style="color:#C792EA;">const</span><span style="color:#A6ACCD;"> tiledTile </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> node</span><span style="color:#89DDFF;">.</span><span style="color:#82AAFF;">addComponent</span><span style="color:#A6ACCD;">(TiledTile)</span><span style="color:#89DDFF;">;</span></span></code></pre></div><h4 id="通过-gettiledtileat-获取-tiledtile" tabindex="-1">通过 getTiledTileAt 获取 TiledTile <a class="header-anchor" href="#通过-gettiledtileat-获取-tiledtile" aria-label="Permalink to &quot;通过 getTiledTileAt 获取 TiledTile&quot;">​</a></h4><div class="language-ts"><button title="Copy Code" class="copy"></button><span class="lang">ts</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#676E95;font-style:italic;">// 获取 layer 上横向坐标为 0，纵向坐标为 0 的 TiledTile 对象，就可以对 TiledTile 对象进行一系列操作</span></span>
<span class="line"><span style="color:#C792EA;">const</span><span style="color:#A6ACCD;"> tiledTile </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">this.</span><span style="color:#A6ACCD;">layer</span><span style="color:#89DDFF;">.</span><span style="color:#82AAFF;">getTiledTileAt</span><span style="color:#A6ACCD;">(</span><span style="color:#F78C6C;">0</span><span style="color:#89DDFF;">,</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">0</span><span style="color:#A6ACCD;">)</span><span style="color:#89DDFF;">;</span></span></code></pre></div><p>Layer 脚本接口相关请参考 <a href="./__APIDOC__/zh/class/TiledLayer.html">TiledLayer API</a></p><h2 id="tiledtile-属性" tabindex="-1">TiledTile 属性 <a class="header-anchor" href="#tiledtile-属性" aria-label="Permalink to &quot;TiledTile 属性&quot;">​</a></h2><table><thead><tr><th style="text-align:left;">属性</th><th style="text-align:left;">功能说明</th></tr></thead><tbody><tr><td style="text-align:left;">X</td><td style="text-align:left;">指定 TiledTile 的横向坐标，以地图块为单位</td></tr><tr><td style="text-align:left;">Y</td><td style="text-align:left;">指定 TiledTile 的纵向坐标，以地图块为单位</td></tr><tr><td style="text-align:left;">Gid</td><td style="text-align:left;">指定 TiledTile 的 gid 值，来切换 TiledTile 的样式</td></tr></tbody></table><p>TiledTile 可以控制指定的地图块，以及将节点的位移、旋转和缩放等应用到地图块。用户可以通过更改 TiledTile 的 gid 属性来更换地图块样式。</p><blockquote><p><strong>注意</strong>: 只能使用地图中现有地图块的 gid 来切换地图块的样式，无法通过自定义 Sprite Frame 来切换地图块的样式。</p></blockquote><h2 id="可作用到-tiledtile-上的节点属性" tabindex="-1">可作用到 TiledTile 上的节点属性 <a class="header-anchor" href="#可作用到-tiledtile-上的节点属性" aria-label="Permalink to &quot;可作用到 TiledTile 上的节点属性&quot;">​</a></h2><table><thead><tr><th style="text-align:left;">属性</th><th style="text-align:left;">功能说明</th></tr></thead><tbody><tr><td style="text-align:left;">Position</td><td style="text-align:left;">可对指定的 TiledTile 进行 <strong>平移</strong> 操作</td></tr><tr><td style="text-align:left;">Rotation</td><td style="text-align:left;">可对指定的 TiledTile 进行 <strong>旋转</strong> 操作</td></tr><tr><td style="text-align:left;">Scale</td><td style="text-align:left;">可对指定的 TiledTile 进行 <strong>缩放</strong> 操作</td></tr><tr><td style="text-align:left;">Color</td><td style="text-align:left;">可对指定的 TiledTile 进行更改 <strong>颜色</strong> 操作</td></tr><tr><td style="text-align:left;">Opacity</td><td style="text-align:left;">可对指定的 TiledTile 调整 <strong>不透明度</strong></td></tr><tr><td style="text-align:left;">Skew</td><td style="text-align:left;">可对指定的 TiledTile 调整 <strong>倾斜角度</strong></td></tr></tbody></table>`,21),d=[o];function r(p,A,c,T,y,f){return t(),e("div",null,d)}const g=l(n,[["render",r]]);export{h as __pageData,g as default};
