import{_ as e,v as s,b as a,R as n}from"./chunks/framework.5ffcbaff.js";const _=JSON.parse('{"title":"网格（Meshes）","description":"","frontmatter":{},"headers":[],"relativePath":"zh/manual/module-map/mesh/index.md","filePath":"zh/manual/module-map/mesh/index.md"}'),r={name:"zh/manual/module-map/mesh/index.md"},t=n('<h1 id="网格-meshes" tabindex="-1">网格（Meshes） <a class="header-anchor" href="#网格-meshes" aria-label="Permalink to &quot;网格（Meshes）&quot;">​</a></h1><p><strong>网格</strong> 一般用于绘制 3D 图像。Creator 提供了以下网格渲染器组件来渲染基础网格、蒙皮网格等，从而将模型绘制显示出来：</p><ul><li><a href="./../../engine/renderable/model-component.html">MeshRenderer</a>：网格渲染器组件，用于渲染基础的模型网格。</li><li><a href="./skinnedMeshRenderer.html">SkinnedMeshRenderer</a>：蒙皮网格渲染器组件，用于渲染蒙皮模型网格。</li><li><a href="./skinnedMeshBatchRenderer.html">SkinnedMeshBatchRenderer</a>：批量蒙皮网格渲染器组件，用于将同一个骨骼动画组件控制的所有子蒙皮模型合并渲染。</li></ul><p>同时，模型若要应用于实际的物理碰撞中，实现类似凹凸不平的路面效果，可以使用网格碰撞组件，会根据模型形状生成碰撞网格。详情请参考 <a href="./../../physics/physics-collider.html#实现鹅软石">使用网格碰撞</a>。</p>',4),h=[t];function d(i,l,o,m,c,p){return s(),a("div",null,h)}const f=e(r,[["render",d]]);export{_ as __pageData,f as default};
