import{_ as t,c as e,o as l,a5 as a}from"./chunks/framework.CqnjdYks.js";const r="/docs/cocos/assets/inspector_1.f5pglyif.png",o="/docs/cocos/assets/inspector_2.ITiMYBix.png",y=JSON.parse('{"title":"粒子系统功能介绍","description":"","frontmatter":{},"headers":[],"relativePath":"zh/manual/particle-system/module.md","filePath":"zh/manual/particle-system/module.md"}'),i={name:"zh/manual/particle-system/module.md"},d=a('<h1 id="粒子系统功能介绍" tabindex="-1">粒子系统功能介绍 <a class="header-anchor" href="#粒子系统功能介绍" aria-label="Permalink to &quot;粒子系统功能介绍&quot;">​</a></h1><p>Cocos Creator 的 <strong>粒子系统</strong> 存储了粒子发射的初始状态以及粒子发射后的状态更新子模块。</p><h2 id="粒子系统模块" tabindex="-1">粒子系统模块 <a class="header-anchor" href="#粒子系统模块" aria-label="Permalink to &quot;粒子系统模块&quot;">​</a></h2><p>Cocos Creator 粒子系统操作面板如下：</p><p><img src="'+r+'" alt="inspector_1"><br><img src="'+o+'" alt="inspector_2"></p><p>粒子系统使用模块来组织功能，包括以下模块：</p><table><thead><tr><th style="text-align:left;">模块</th><th style="text-align:left;">说明</th></tr></thead><tbody><tr><td style="text-align:left;">Node</td><td style="text-align:left;">粒子节点，用于设置粒子发射器的位置、方向、大小、渲染层级等属性</td></tr><tr><td style="text-align:left;"><a href="./main-module.html">ParticleSystem</a>（主模块）</td><td style="text-align:left;">用于存储在 <strong>属性检查器</strong> 中显示的所有数据，管理粒子生成、更新、销毁相关模块，控制粒子播放</td></tr><tr><td style="text-align:left;"><a href="./emitter.html">ShapeModule</a>（发射器模块）</td><td style="text-align:left;">用于控制粒子发射，包括发射方向与速度，支持预定义的发射方向，包括方块、圆、锥体、球、半球</td></tr><tr><td style="text-align:left;">AnimatorModule（状态模块）</td><td style="text-align:left;">用于控制粒子发射后的状态更新。已支持的功能包括：<br><a href="./noise-module.html">噪声模块（NoiseModule）</a><br><a href="./velocity-module.html">速度模块（VelocityOvertimeModule）</a><br><a href="./force-module.html">加速模块（ForceOvertimeModule）</a><br><a href="./size-module.html">大小模块（SizeOvertimeModule）</a><br><a href="./rotation-module.html">旋转模块（RotationOvertimeModule）</a><br><a href="./color-module.html">颜色模块（ColorOvertimeModule）</a><br><a href="./texture-animation-module.html">贴图动画模块（TextureAnimationModule）</a><br><a href="./limit-velocity-module.html">限速模块（LimitVelocityOvertimeModule）</a><br><a href="./trail-module.html">拖尾模块（TrailModule）</a></td></tr><tr><td style="text-align:left;"><a href="./renderer.html">Renderer</a></td><td style="text-align:left;">用于生成粒子渲染所需要的数据。包括 VB、IB、渲染状态相关的控制</td></tr></tbody></table>',7),s=[d];function n(m,c,h,u,f,_){return l(),e("div",null,s)}const g=t(i,[["render",n]]);export{y as __pageData,g as default};
