import{_ as t}from"./chunks/menu-button.CjNMAp13.js";import{_ as e,c as n,o as r,a5 as o}from"./chunks/framework.CqnjdYks.js";const a="/docs/cocos/assets/texture_animation.hbr8Op-N.gif",s="/docs/cocos/assets/texture_animation.Cfx3s6Xv.png",y=JSON.parse('{"title":"贴图动画模块（TextureAnimationModule）","description":"","frontmatter":{},"headers":[],"relativePath":"zh/manual/particle-system/texture-animation-module.md","filePath":"zh/manual/particle-system/texture-animation-module.md"}'),l={name:"zh/manual/particle-system/texture-animation-module.md"},i=o('<h1 id="贴图动画模块-textureanimationmodule" tabindex="-1">贴图动画模块（TextureAnimationModule） <a class="header-anchor" href="#贴图动画模块-textureanimationmodule" aria-label="Permalink to &quot;贴图动画模块（TextureAnimationModule）&quot;">​</a></h1><p>贴图动画模块用于将 <a href="./renderer.html">渲染模块</a> 中 <strong>ParticleMaterial</strong> 属性指定的贴图纹理作为动画帧进行动态播放，用于实现类似下图中的效果：</p><p><img src="'+a+'" alt="texture_animation"></p><h2 id="属性" tabindex="-1">属性 <a class="header-anchor" href="#属性" aria-label="Permalink to &quot;属性&quot;">​</a></h2><p><img src="'+s+'" alt="texture_animation"></p><table><thead><tr><th style="text-align:left;">属性</th><th style="text-align:left;">说明</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>Mode</strong></td><td style="text-align:left;">设定粒子动画贴图的类型，目前只支持 <strong>Grid</strong>（网格）模式。一张贴图包含一个粒子播放的动画帧。</td></tr><tr><td style="text-align:left;"><strong>NumTilesX</strong></td><td style="text-align:left;">贴图纹理在水平（X）方向上划分的贴图数量。</td></tr><tr><td style="text-align:left;"><strong>NumTilesY</strong></td><td style="text-align:left;">贴图纹理在垂直（Y）方向上划分的贴图数量。</td></tr><tr><td style="text-align:left;"><strong>Animation</strong></td><td style="text-align:left;">动画播放方式，包括：<br> <strong>WholeSheet</strong>：播放贴图中的所有帧；<br><strong>SingleRow</strong>：只播放其中一行，默认第一行。可搭配 <strong>RandomRow</strong> 和 <strong>RowIndex</strong> 属性使用。</td></tr><tr><td style="text-align:left;"><strong>RandomRow</strong></td><td style="text-align:left;">随机从动画贴图中选择一行播放动画帧。<br>该项仅在 <strong>Animation</strong> 设置为 <strong>SingleRow</strong> 时生效。</td></tr><tr><td style="text-align:left;"><strong>RowIndex</strong></td><td style="text-align:left;">从动画贴图中选择特定行以播放动画帧。<br>该项仅在 <strong>Animation</strong> 设置为 <strong>SingleRow</strong> 并且禁用 <strong>RandomRow</strong> 时生效。</td></tr><tr><td style="text-align:left;"><strong>FrameOverTime</strong></td><td style="text-align:left;">设置动画播放速度。<br>当点击输入框右侧的 <img src="'+t+'" alt="menu button"> 按钮，切换使用曲线编辑时，表示一个周期内动画播放的帧与时间变化曲线。</td></tr><tr><td style="text-align:left;"><strong>StartFrame</strong></td><td style="text-align:left;">指定动画在整个粒子系统生命周期的第几帧开始播放。</td></tr><tr><td style="text-align:left;"><strong>CycleCount</strong></td><td style="text-align:left;">动画帧在粒子生命周期内重复播放的次数。</td></tr></tbody></table>',6),d=[i];function g(m,x,c,u,_,f){return r(),n("div",null,d)}const b=e(l,[["render",g]]);export{y as __pageData,b as default};
