import{_ as t,v as e,b as r,R as l}from"./chunks/framework.5ffcbaff.js";import{_ as s}from"./chunks/menu-button.cd99b016.js";const a="/docs/cocos/assets/trail.759bb07b.gif",o="/docs/cocos/assets/trail_module.f5b15024.png",_=JSON.parse('{"title":"轨迹模块（TrailModule）","description":"","frontmatter":{},"headers":[],"relativePath":"zh/manual/particle-system/trail-module.md","filePath":"zh/manual/particle-system/trail-module.md"}'),n={name:"zh/manual/particle-system/trail-module.md"},d=l('<h1 id="轨迹模块-trailmodule" tabindex="-1">轨迹模块（TrailModule） <a class="header-anchor" href="#轨迹模块-trailmodule" aria-label="Permalink to &quot;轨迹模块（TrailModule）&quot;">​</a></h1><p>轨迹模块用于在粒子尾部添加一个轨迹效果，实现类似下图中的拖尾效果：</p><p><img src="'+a+'" alt="trail_module"></p><h2 id="属性" tabindex="-1">属性 <a class="header-anchor" href="#属性" aria-label="Permalink to &quot;属性&quot;">​</a></h2><p><img src="'+o+'" alt="trail_module"></p><table><thead><tr><th style="text-align:left;">属性</th><th style="text-align:left;">说明</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>Mode</strong></td><td style="text-align:left;">粒子系统生成拖尾的方式，目前仅支持 <strong>Particles</strong>，表示在每个粒子的运动轨迹上都行成拖尾效果。</td></tr><tr><td style="text-align:left;"><strong>LifeTime</strong></td><td style="text-align:left;">生成的拖尾的生命周期。</td></tr><tr><td style="text-align:left;"><strong>MinParticleDistance</strong></td><td style="text-align:left;">粒子每生成一个拖尾节点所运行的最短距离。</td></tr><tr><td style="text-align:left;"><strong>Space</strong></td><td style="text-align:left;">选择拖尾运行时基于 <strong>世界坐标系</strong>（World）或者 <strong>本地坐标系</strong>（Local）<br>（<strong>Custom</strong> 目前暂不支持）</td></tr><tr><td style="text-align:left;"><strong>TextureMode</strong></td><td style="text-align:left;">当在 <a href="./renderer.html">渲染模块</a> 中指定了 <strong>TrialMaterial</strong> 贴图时，贴图在拖尾上的展开形式。目前仅支持 <strong>Stretch</strong>，表示将贴图覆盖在整条拖尾上。</td></tr><tr><td style="text-align:left;"><strong>WidthFromParticle</strong></td><td style="text-align:left;">拖尾宽度跟随粒子大小</td></tr><tr><td style="text-align:left;"><strong>WidthRatio</strong></td><td style="text-align:left;">拖尾宽度。若勾选 <strong>WidthFromParticle</strong>，则拖尾宽度为例子大小乘以比例。</td></tr><tr><td style="text-align:left;"><strong>ColorFromParticle</strong></td><td style="text-align:left;">拖尾颜色是否跟随粒子初始颜色</td></tr><tr><td style="text-align:left;"><strong>ColorOverTrail</strong></td><td style="text-align:left;">拖尾颜色随着拖尾自身长度的变化而变化</td></tr><tr><td style="text-align:left;"><strong>ColorOvertime</strong></td><td style="text-align:left;">拖尾颜色随着时间的变化而变化</td></tr></tbody></table><p>点击属性输入框右侧的 <img src="'+s+'" alt="menu button"> 按钮，可选择对属性进行曲线编辑/渐变色编辑，详情请参考 <a href="./editor/">粒子属性编辑</a>。</p>',7),i=[d];function g(c,m,f,h,u,x){return e(),r("div",null,i)}const b=t(n,[["render",g]]);export{_ as __pageData,b as default};
