import{_ as e,c as a,o as s,a4 as n}from"./chunks/framework.uQk9_EO2.js";const f=JSON.parse('{"title":"网格（Meshes）","description":"","frontmatter":{},"headers":[],"relativePath":"zh/module-map/mesh/index.md","filePath":"zh/module-map/mesh/index.md","lastUpdated":1712305443000}'),r={name:"zh/module-map/mesh/index.md"},t=n('<h1 id="网格-meshes" tabindex="-1">网格（Meshes） <a class="header-anchor" href="#网格-meshes" aria-label="Permalink to &quot;网格（Meshes）&quot;">​</a></h1><p><strong>网格</strong> 一般用于绘制 3D 图像。Creator 提供了以下网格渲染器组件来渲染基础网格、蒙皮网格等，从而将模型绘制显示出来：</p><ul><li><a href="./../../engine/renderable/model-component">MeshRenderer</a>：网格渲染器组件，用于渲染基础的模型网格。</li><li><a href="./skinnedMeshRenderer">SkinnedMeshRenderer</a>：蒙皮网格渲染器组件，用于渲染蒙皮模型网格。</li><li><a href="./skinnedMeshBatchRenderer">SkinnedMeshBatchRenderer</a>：批量蒙皮网格渲染器组件，用于将同一个骨骼动画组件控制的所有子蒙皮模型合并渲染。</li></ul><p>同时，模型若要应用于实际的物理碰撞中，实现类似凹凸不平的路面效果，可以使用网格碰撞组件，会根据模型形状生成碰撞网格。详情请参考 <a href="./../../physics/physics-collider#实现鹅软石">使用网格碰撞</a>。</p>',4),d=[t];function o(h,i,l,c,_,m){return s(),a("div",null,d)}const u=e(r,[["render",o]]);export{f as __pageData,u as default};
