import{_ as t,c as e,o as l,a4 as a}from"./chunks/framework.uQk9_EO2.js";const r="/docs/cocos/assets/unlit-shademode.BGso6kc9.png",u=JSON.parse('{"title":"无光照","description":"","frontmatter":{},"headers":[],"relativePath":"zh/shader/effect-builtin-unlit.md","filePath":"zh/shader/effect-builtin-unlit.md","lastUpdated":1712305443000}'),i={name:"zh/shader/effect-builtin-unlit.md"},d=a('<h1 id="无光照" tabindex="-1">无光照 <a class="header-anchor" href="#无光照" aria-label="Permalink to &quot;无光照&quot;">​</a></h1><p>无光照是最基础的着色模型，这种模型下，引擎的任何光源都无法影响其最终效果，适用于：</p><ul><li>不受光源影响的物体</li><li>画面要求不高或性能要求高的场景</li></ul><p>在材质的 <strong>Effect</strong> 属性中将着色器切换为 Cocos Creator 内置的无光照着色器（builtin-unlit.effect）时，如下图：</p><p><img src="'+r+'" alt="unlit"></p><h2 id="制作标准" tabindex="-1">制作标准 <a class="header-anchor" href="#制作标准" aria-label="Permalink to &quot;制作标准&quot;">​</a></h2><p>技术选型时，若要在使用 <strong>unlit</strong> 材质的模型下使用光照，可将光照信息绘制在纹理贴图上，然后将纹理贴图拖拽到材质的 <strong>MainTexture</strong> 属性框中。</p><p>若要使用真实光照，可参考：<a href="./effect-builtin-pbr">真实物理渲染</a></p><h2 id="参数" tabindex="-1">参数 <a class="header-anchor" href="#参数" aria-label="Permalink to &quot;参数&quot;">​</a></h2><table><thead><tr><th style="text-align:left;">参数</th><th style="text-align:left;">说明</th></tr></thead><tbody><tr><td style="text-align:left;">mainTexture</td><td style="text-align:left;">主纹理</td></tr><tr><td style="text-align:left;">tilingOffset</td><td style="text-align:left;">模型 UV 的缩放和偏移量，xy 对应缩放，zw 对应偏移</td></tr><tr><td style="text-align:left;">mainColor</td><td style="text-align:left;">主颜色，该颜色会在片元着色器内被处理</td></tr><tr><td style="text-align:left;">colorScale</td><td style="text-align:left;">和主颜色相乘</td></tr><tr><td style="text-align:left;">alphaThreshold</td><td style="text-align:left;">用于半透明测试，在启用 USE_ALPHA_TEST 的情况下，小于该值的像素会被抛弃（discard）</td></tr></tbody></table><h2 id="宏" tabindex="-1">宏 <a class="header-anchor" href="#宏" aria-label="Permalink to &quot;宏&quot;">​</a></h2><table><thead><tr><th style="text-align:left;">定义</th><th style="text-align:left;">说明</th></tr></thead><tbody><tr><td style="text-align:left;">USE_INSTANCING</td><td style="text-align:left;">是否启用几何体实例化</td></tr><tr><td style="text-align:left;">USE_VERTEX_COLOR</td><td style="text-align:left;">是否叠加顶点颜色和 Alpha 值</td></tr><tr><td style="text-align:left;">USE_TEXTURE</td><td style="text-align:left;">是否使用主纹理（mainTexture）</td></tr><tr><td style="text-align:left;">USE_ALPHA_TEST</td><td style="text-align:left;">是否进行半透明测试（AlphaTest）</td></tr><tr><td style="text-align:left;">SAMPLE_FROM_RT</td><td style="text-align:left;">是否是从 RenderTexture 中采样，勾选后会对 UV 的 Y 值进行翻转</td></tr></tbody></table>',12),n=[d];function s(o,f,h,c,_,x){return l(),e("div",null,n)}const y=t(i,[["render",s]]);export{u as __pageData,y as default};
