import{_ as t,c as e,o as r,a4 as a}from"./chunks/framework.uQk9_EO2.js";const l="/docs/cocos/assets/tracking-origin.CXsuShoy.png",s="/docs/cocos/assets/hdmctrl.BQ7KUT-I.png",d="/docs/cocos/assets/pose-tracker.By4gQIxy.png",n="data:image/png;base64,",o="/docs/cocos/assets/xrcontroller.BmBwyEgH.png",O=JSON.parse('{"title":"设备映射组件","description":"","frontmatter":{},"headers":[],"relativePath":"zh/xr/vr/device-mapping.md","filePath":"zh/xr/vr/device-mapping.md","lastUpdated":1712305443000}'),i={name:"zh/xr/vr/device-mapping.md"},g=a('<h1 id="设备映射组件" tabindex="-1">设备映射组件 <a class="header-anchor" href="#设备映射组件" aria-label="Permalink to &quot;设备映射组件&quot;">​</a></h1><p>此类组件主要用以同步现实世界中物理设备和虚拟世界中的代理节点之间的 I/O 信息。确保在用户在 XR 设备的使用和虚拟世界中的反馈一致。</p><p>主要包括以下组件：</p><h2 id="trackingorigin" tabindex="-1">TrackingOrigin <a class="header-anchor" href="#trackingorigin" aria-label="Permalink to &quot;TrackingOrigin&quot;">​</a></h2><p>追踪原点代理组件。</p><p><img src="'+l+'" alt="tracking-origin.png"></p><table><thead><tr><th style="text-align:left;">属性</th><th style="text-align:left;">说明</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>Offset Object</strong></td><td style="text-align:left;">指定需要竖直偏移的对象，如果选择的对象还有子对象，则偏移的效果是选中对象及其所有子对象进行纵向偏移。</td></tr><tr><td style="text-align:left;"><strong>Tracking Origin Mode</strong></td><td style="text-align:left;">追踪的偏移方式。选择 Unbond 和 Device 时，下方出现 YOffsetValue，可手动输入数据；选择 Floor 时YoffsetValue 隐藏。如果选择为 Floor，且设备开启了安全边界，则以设备离地面高度作为当前视角高度（暂时只支持 quest2 ）；如果选择为Device，则偏移的高度为输入的高度。</td></tr><tr><td style="text-align:left;"><strong>YOffset Value</strong></td><td style="text-align:left;">设备数值偏移量。手动输入偏移的值，米为单位，默认为1.36144m。如果为固定值，则OffsetObject选中的对象Transform属性的Y值为当前填入的值。</td></tr></tbody></table><h2 id="hmdctrl" tabindex="-1">HMDCtrl <a class="header-anchor" href="#hmdctrl" aria-label="Permalink to &quot;HMDCtrl&quot;">​</a></h2><p>HMD（Head Mounted Display）头戴显示设备控制器。该组件抽象了头戴设备的渲染相关的参数。</p><p><img src="'+s+'" alt="hmdctrl"></p><table><thead><tr><th style="text-align:left;">属性</th><th style="text-align:left;">说明</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>Stereo Render</strong></td><td style="text-align:left;"></td></tr><tr><td style="text-align:left;"><strong>Per Eye Camera</strong></td><td style="text-align:left;">开启单眼显示功能；勾选 PerEyeCamera 后下方出现 Sync With Main Camera 选项；XR HMD 下的两个子节点 LeftEye 和 RightEye 由隐藏变为显示</td></tr><tr><td style="text-align:left;"><strong>Sync With Main Camera</strong></td><td style="text-align:left;">同步主摄像头参数；设置为 on 时，XR HMD 下的两个子节点 LeftEye 和 RightEye 的 camera，会进行一次同步 XR HMD 的 camera 属性</td></tr><tr><td style="text-align:left;"><strong>IPDOffset</strong></td><td style="text-align:left;">调整瞳间距。下拉列表中选择 Manual 时下方出现 OffsetValue 输入框；在 PerEyeCamera 开启的情况下，调整 Manual 的参数可让 LeftEye 和 RightEye 的 TransForm 属性 X 值变化（变化的值为 ±IPDOffset/2）</td></tr><tr><td style="text-align:left;"><strong>Aspect Ratio</strong></td><td style="text-align:left;"></td></tr></tbody></table><h2 id="posetracker" tabindex="-1">PoseTracker <a class="header-anchor" href="#posetracker" aria-label="Permalink to &quot;PoseTracker&quot;">​</a></h2><p>位姿追踪组件。</p><p><img src="'+d+'" alt="pose-tracker"></p><table><thead><tr><th style="text-align:left;">属性</th><th style="text-align:left;">说明</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>Tracking Source</strong></td><td style="text-align:left;">选择需要追踪的设备源</td></tr><tr><td style="text-align:left;"><strong>Tracking Type</strong></td><td style="text-align:left;">追踪模式。选择 POSITION_AND_ROTATION 时追踪设备的平移 + 旋转；选择 POSITION 时只追踪平移的量；选择 ROTATION 时只追踪旋转的量。</td></tr></tbody></table><h2 id="targeteye" tabindex="-1">TargetEye <a class="header-anchor" href="#targeteye" aria-label="Permalink to &quot;TargetEye&quot;">​</a></h2><p>指定接受渲染相机的组件。</p><p><img src="'+n+'" alt="target-eye"></p><table><thead><tr><th style="text-align:left;">属性</th><th style="text-align:left;">说明</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>Target Eye</strong></td><td style="text-align:left;">指定渲染的目镜。Both 为左右眼都显示，left 为左眼，right 为右眼。</td></tr></tbody></table><h2 id="xrcontroller" tabindex="-1">XRController <a class="header-anchor" href="#xrcontroller" aria-label="Permalink to &quot;XRController&quot;">​</a></h2><p>控制器抽象组件。</p><p><img src="'+o+'" alt="xrcontroller"></p><table><thead><tr><th style="text-align:left;">属性</th><th style="text-align:left;">说明</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>InputDevice</strong></td><td style="text-align:left;">绑定输入手柄设备。</td></tr><tr><td style="text-align:left;"><strong>SelectAction</strong></td><td style="text-align:left;">将 Select 行为映射到手柄的对应实体按键</td></tr><tr><td style="text-align:left;"><strong>ActivateAction</strong></td><td style="text-align:left;">将 Activate 行为映射到手柄的对应实体按键</td></tr><tr><td style="text-align:left;"><strong>UIPressAction</strong></td><td style="text-align:left;">将交互 UI 行为映射到手柄的对应实体按键</td></tr><tr><td style="text-align:left;"><strong>AxisToPressThreshold</strong></td><td style="text-align:left;">行为触发时的阈值</td></tr><tr><td style="text-align:left;"><strong>Model</strong></td><td style="text-align:left;">用于指定手柄的模型对象。</td></tr></tbody></table>',23),c=[g];function f(y,h,x,p,A,b){return r(),e("div",null,c)}const u=t(i,[["render",f]]);export{O as __pageData,u as default};
